import './Tab.css';
import { useState } from 'react';

function Tab( { entries }) {
    const [isEntryOpen, setIsEntryOpen] = useState({
        'Wet Noses': true
    })

    function toggleEntry(title) {
        setIsEntryOpen({
        ...setIsEntryOpen,
        [title]: !setIsEntryOpen[title],
        });
    }

    return (
        <div className="tab">
            {
                Object.keys(entries).map( title => {
                    const isOpen = isEntryOpen[title];

                    return ( 
                    <div className='tab__tabs'>
                        <div key={title} className="tab__entry">
                        <button 
                            onClick={
                                () => toggleEntry(title)
                            }
                            className="tab__title">
                                {title}
                        </button>
                        </div>  
                        {isOpen && <div className="tab__body">{entries[title]}</div>
                        }
                    </div>
                    );
                })
            }   
        </div>
    );
}

export default Tab;
